mapper.update:
update user
set last_login_time = current_timestamp 
where username = #{username}

update user
set password = #{password},
salt = #{salt}
where user_id = #{userId}

mapper.select:
select count(*)
from user
where username = #{username}

select count(*)
from user
where username = #{username}
and user_id != #{userId}

select user.user_id, user.username, user.`password`, user.salt, user.`status`, user.last_login_time, user.create_time,
user.modify_time, user.email, user.phone, user.student_id, role.role_name
from user,user_role,role
where user.user_id = user_role.user_id
and user_role.role_id = role.role_id

    and user_id = #{userId}


    and username = #{username}


    and status = #{status}


    and email = #{email}


    and phone = #{phone}


    and student_id = #{studentId}
